  depress_excl_2<-ifelse(substring(x, 1,4) %in% c("F301", "F302", "F323", "F333", "F311", "F312", "F313", "F314", "F315", "F316", "F319", "F348", "F208", "F201", "F202", "F200", "F208", "F205", "F259", "F209"),1,0)
